void WorldDB::remove(string name) {
	for (int i = 0; i < worlds.size(); i++)
	{
		WorldInfo w = worlds.at(i);
		if (getStrLower(name) == getStrLower(w.name))
		{
			worlds.erase(worlds.begin() + i);
		}
	}
	std::remove(("worlds/" + name + ".json").c_str());
}
